(function() {
    
    tinymce.PluginManager.requireLangPack("fontawesome"),
    tinymce.PluginManager.add('fontawesome', function(editor, url) {

        editor.addButton('icons', {
            text : 'Icons',
            stateSelector: "i",
            icon : 'mfn-icons',
            onclick : function() {
                $('#iconsModal').modal('show');
            }
        });

    });
    
})();